#include "JuegoLibreria.h"

int main() {
    srand(time(NULL));
    cargarEscenario("escenario.txt");
    dibujarPersonaje();
    mostrarEscenarioConPuntaje();

    while (1) {
        if (_kbhit()) {
            char tecla = _getch();

            if (tecla == 27) {  
                printf("Saliendo del juego...\n");
                break;
            }

            switch (tecla) {
                case 'a': 
                    direccionPistola = '+';  
                    moverIzquierda(); 
                    break;
                case 'd': 
                    direccionPistola = '-';  
                    moverDerecha(); 
                    break;
                case 'w': 
                    saltar(); 
                    break;  
                case 's': 
                    moverAbajo(); 
                    break; 
                case 'p': 
                    disparar();  
                    break;
            }
            aplicarGravedad();
            verificarLimiteJugador();
            if (rand() % 10 < 2 && numEnemigos < 10) {
                generarEnemigos();
            }
            moverEnemigos();
            verificarLimiteEnemigos();
            verificarColisionConEnemigos();
            mostrarEscenarioConPuntaje();
        }
    }
    return 0;
}



